import React, { useState, useEffect } from 'react';
import axios from 'axios'; 
import './Movies.css';
import Carousel from '../components/Carousel';

const Movies = () => {
  const [topRated, setTopRated] = useState([]);
  const [latestReleases, setLatestReleases] = useState([]);
  const [upcomingMovies, setUpcomingMovies] = useState([]);

  useEffect(() => {
    axios.get('https://api.example.com/movie/top_rated')
      .then(response => setTopRated(response.data.results))
      .catch(error => console.error('Error fetching top-rated movies:', error));

    axios.get('https://api.example.com/movie/popular') 
      .then(response => setLatestReleases(response.data.results))
      .catch(error => console.error('Error fetching latest releases:', error));

    axios.get('https://api.example.com/movie/upcoming')
      .then(response => setUpcomingMovies(response.data.results))
      .catch(error => console.error('Error fetching upcoming movies:', error));
  }, []);

  return (
    <div className="movies-container">
      <section className="movies-section">
        <h2>Top Rated Movies</h2>
        <Carousel movies={topRated} />
      </section>
      
      <section className="movies-section">
        <h2>Latest Releases</h2>
        <Carousel movies={latestReleases} />
      </section>
      
      <section className="movies-section">
        <h2>Upcoming Movies</h2>
        <Carousel movies={upcomingMovies} />
      </section>
    </div>
  );
};

export default Movies;
