import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { fetchMovieDetails, fetchTVShowDetails } from '../utils/tmdb';
import './DetailPage.css';

const DetailPage = () => {
  const { id, type } = useParams(); // Assumes type is included in the URL to differentiate movie and TV show
  const [item, setItem] = useState(null);

  useEffect(() => {
    const fetchDetails = async () => {
      try {
        let data;
        if (type === 'movie') {
          data = await fetchMovieDetails(id);
        } else if (type === 'tv') {
          data = await fetchTVShowDetails(id);
        }
        setItem(data);
      } catch (error) {
        console.error('Error fetching details:', error);
      }
    };

    fetchDetails();
  }, [id, type]);

  if (loading) return <div>Loading...</div>;
  if (!item) return <div>Loading...</div>;

  return (
    <div className="detail-page">
      <h1>{item.title || item.name}</h1>
      <div className="detail-content">
        <img src={`https://image.tmdb.org/t/p/w500${item.poster_path}`} alt={item.title || item.name} />
        <div className="detail-info">
          <p><strong>Rating:</strong> {item.vote_average}</p>
          <p><strong>Release Date:</strong> {item.release_date || item.first_air_date}</p>
          <p><strong>Overview:</strong> {item.overview}</p>
          <p><strong>Cast:</strong> {/* Display cast information if available */}</p>
          <p><strong>Creator/Director:</strong> {/* Display creator/director information if available */}</p>
          <p><strong>Genre:</strong> {item.genres.map(genre => genre.name).join(', ')}</p>
          <p><strong>Runtime:</strong> {item.runtime || item.episode_run_time.join(', ')} minutes</p>
        </div>
      </div>
    </div>
  );
};

export default DetailPage;
