import React, { useEffect, useState } from 'react';
import { fetchLatestMovies, fetchLatestTVShows } from '../utils/tmdb';
import MovieCard from '../components/MovieCard';
import TVShowCard from '../components/TVShowCard'; // Create a separate card component for TV shows
import './Trending.css'; // Ensure this CSS file includes styles for both sections

const TrendingSection = () => {
  const [latestMovies, setLatestMovies] = useState([]);
  const [latestTVShows, setLatestTVShows] = useState([]);

  useEffect(() => {
    const getLatestData = async () => {
      try {
        const moviesData = await fetchLatestMovies();
        const tvShowsData = await fetchLatestTVShows();

        if (moviesData && moviesData.results) {
          setLatestMovies(moviesData.results);
        }
        
        if (tvShowsData && tvShowsData.results) {
          setLatestTVShows(tvShowsData.results);
        }
      } catch (error) {
        console.error('Error fetching latest data:', error);
      }
    };

    getLatestData();
  }, []);

  return (
    <div className="trending-section">
      <div className="latest-movies-section">
        <h2 className="latest-movies-title">Latest Movies</h2>
        <div className="latest-movies-carousel">
          {latestMovies.length > 0 ? (
            latestMovies.map((movie) => (
              <MovieCard key={movie.id} movie={movie} />
            ))
          ) : (
            <p>Loading...</p>
          )}
        </div>
      </div>

      <div className="latest-tv-shows-section">
        <h2 className="latest-tv-shows-title">Latest TV Shows</h2>
        <div className="latest-tv-shows-carousel">
          {latestTVShows.length > 0 ? (
            latestTVShows.map((show) => (
              <TVShowCard key={show.id} show={show} />
            ))
          ) : (
            <p>Loading...</p>
          )}
        </div>
      </div>
    </div>
  );
};

export default TrendingSection;
