import React, { useEffect, useState } from 'react';
import { fetchTrending } from '../utils/tmdb';
import TrendingCarousel from '../components/Carousel';
import './Trending.css';

const TrendingSection = () => {
  const [trending, setTrending] = useState([]);

  useEffect(() => {
    const getTrending = async () => {
      const data = await fetchTrending('/trending/all/day'); // Adjust the endpoint as needed
      if (data && data.results) {
        setTrending(data.results);
      } else {
        console.error('Failed to fetch trending content');
      }
    };

    getTrending();
  }, []);

  return (
    <div className="trending-section">
      <h2 className="trending-title">Explore What's Trending</h2>
      <TrendingCarousel movies={trending} />
    </div>
  );
};

export default TrendingSection;
