const fetchUpcomingMoviesAndTVShows = async () => {
  try {
      // Fetch upcoming movies and TV shows separately
      const currentDate = new Date().toISOString().split('T')[0]; 
      const upcomingMoviesResponse = await axios.get(
          `${BASE_URL}/movie/upcoming?api_key=${API_KEY}&language=en-US&sort_by=release_date.asc&release_date.gte=${currentDate}&include_adult=true`
      );

      const upcomingTVShowsResponse = await axios.get(
          `${BASE_URL}/tv/on_the_air?api_key=${API_KEY}&language=en-US&sort_by=first_air_date.asc&first_air_date.gte=${currentDate}&include_adult=true`
      );

      // Combine movies and TV shows
      const upcomingMovies = upcomingMoviesResponse.data.results || [];
      const upcomingTVShows = upcomingTVShowsResponse.data.results || [];

      // Combine and filter by popularity and genre
      const combinedResults = [...upcomingMovies, ...upcomingTVShows].filter(item => 
          item.popularity > 10 &&  
          (item.genre_ids.includes(28) || 
          item.genre_ids.includes(35) || 
          item.genre_ids.includes(12))  
      );

      return combinedResults;
  } catch (error) {
      console.error('Error fetching upcoming movies and TV shows:', error);
      return [];
  }
};

export { fetchMovies, fetchResultsPage, fetchDetails , fetchLatestMovies, fetchLatestTVShows, fetchUpcomingMoviesAndTVShows};
